import React from 'react';

const Content = ({img, text, link}) => {
	return (
		<div>
			<img alt='hmmm... the content should be here' src={require(`./${img}.jpg`)}/>
			<div className='pa1'> {text} </div>
			<div className='pa1 f7 --light-blue'><a href={link}> {link[5].toLowerCase()==='s'?link.slice(10):link.slice(9)} </a> </div>
		</div>
		)
};

export default Content;
